// src/App.js
import React, { useState } from "react";
import { Canvas } from "@react-three/fiber";
import { OrbitControls, useGLTF } from "@react-three/drei";

const models = [
  { name: "Duck", file: "Duck.glb", image: "Duck.png" },
  { name: "Chair", file: "Chair.glb", image: "Chair.png" },
  { name: "Lamp", file: "Lamp.glb", image: "Lamp.png" },
];

function ModelViewer({ modelPath }) {
  const { scene } = useGLTF(modelPath);
  return <primitive object={scene} />;
}

function App() {
  const [selectedModel, setSelectedModel] = useState(models[0].file);

  return (
    <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
      <h2>GLB Model Viewer</h2>

      <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
        {models.map((model) => (
          <button
            key={model.file}
            onClick={() => setSelectedModel(model.file)}
            style={{
              border: selectedModel === model.file ? "3px solid blue" : "1px solid gray",
              padding: "5px",
              background: "#f5f5f5",
              cursor: "pointer",
            }}
          >
            <img
              src={`${process.env.PUBLIC_URL}/${model.image}`}
              alt={model.name}
              width="100"
              height="100"
            />
            <div>{model.name}</div>
          </button>
        ))}
      </div>

      <Canvas style={{ width: 600, height: 500 }}>
        <ambientLight />
        <pointLight position={[10, 10, 10]} />
        <OrbitControls />
        <Suspense fallback={null}>
          <ModelViewer modelPath={`/${selectedModel}`} />
        </Suspense>
      </Canvas>
    </div>
  );
}

export default App;
