import React, { useState, useEffect, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import * as THREE from 'three';

const models = [
  { name: 'Model 1', file: 'model1.glb', image: 'model1.png' },
  { name: 'Model 2', file: 'model2.glb', image: 'model2.png' },
  { name: 'Model 3', file: 'model3.glb', image: 'model3.png' },
];

function ModelViewer({ modelUrl }) {
  const [scene, setScene] = useState();

  useEffect(() => {
    const loader = new GLTFLoader();
    loader.load(
      `${process.env.PUBLIC_URL}/${modelUrl}`,
      gltf => setScene(gltf.scene),
      undefined,
      error => console.error('Error loading GLB model:', error)
    );
  }, [modelUrl]);

  return scene ? <primitive object={scene} scale={2} /> : null;
}

function App() {
  const [selectedModel, setSelectedModel] = useState(models[0].file);

  return (
    <div style={{ textAlign: 'center' }}>
      <h2>3D Model Viewer</h2>

      <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem', marginBottom: '1rem' }}>
        {models.map((model, index) => (
          <div key={index}>
            <img
              src={`/${model.image}`}
              alt={model.name}
              style={{ width: 100, height: 100, cursor: 'pointer', border: selectedModel === model.file ? '2px solid blue' : '1px solid gray' }}
              onClick={() => setSelectedModel(model.file)}
            />
            <div>{model.name}</div>
          </div>
        ))}
      </div>

      <div style={{ width: '100%', height: '500px' }}>
        <Canvas camera={{ position: [0, 0, 5], fov: 45 }}>
          <ambientLight intensity={0.5} />
          <directionalLight position={[0, 5, 5]} intensity={1} />
          <Suspense fallback={null}>
            <ModelViewer modelUrl={selectedModel} />
            <OrbitControls />
            <Environment preset="sunset" />
          </Suspense>
        </Canvas>
      </div>
    </div>
  );
}

export default App;
